import { type User, type InsertUser, type LoginUser, type ToolResult, type InsertToolResult, type UserSession } from "@shared/schema";
import { randomUUID } from "crypto";
import bcrypt from "bcrypt";

export interface IStorage {
  // User management
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Tool results
  saveToolResult(result: InsertToolResult & { userId?: string }): Promise<ToolResult>;
  getToolResults(userId: string, toolType?: string): Promise<ToolResult[]>;

  // Sessions
  createSession(userId: string, token: string, expiresAt: Date): Promise<UserSession>;
  getSessionByToken(token: string): Promise<UserSession | undefined>;
  deleteSession(token: string): Promise<void>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private toolResults: Map<string, ToolResult>;
  private userSessions: Map<string, UserSession>;

  constructor() {
    this.users = new Map();
    this.toolResults = new Map();
    this.userSessions = new Map();
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.email === email,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const hashedPassword = await bcrypt.hash(insertUser.password, 10);
    const user: User = { 
      ...insertUser, 
      id, 
      password: hashedPassword,
      createdAt: new Date()
    };
    this.users.set(id, user);
    return user;
  }

  async saveToolResult(result: InsertToolResult & { userId?: string }): Promise<ToolResult> {
    const id = randomUUID();
    const toolResult: ToolResult = {
      ...result,
      id,
      userId: result.userId || null,
      createdAt: new Date()
    };
    this.toolResults.set(id, toolResult);
    return toolResult;
  }

  async getToolResults(userId: string, toolType?: string): Promise<ToolResult[]> {
    return Array.from(this.toolResults.values()).filter(
      (result) => result.userId === userId && (!toolType || result.toolType === toolType)
    );
  }

  async createSession(userId: string, token: string, expiresAt: Date): Promise<UserSession> {
    const id = randomUUID();
    const session: UserSession = {
      id,
      userId,
      token,
      expiresAt,
      createdAt: new Date()
    };
    this.userSessions.set(token, session);
    return session;
  }

  async getSessionByToken(token: string): Promise<UserSession | undefined> {
    const session = this.userSessions.get(token);
    if (session && session.expiresAt > new Date()) {
      return session;
    }
    if (session) {
      this.userSessions.delete(token);
    }
    return undefined;
  }

  async deleteSession(token: string): Promise<void> {
    this.userSessions.delete(token);
  }
}

export const storage = new MemStorage();

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, integer, timestamp, jsonb, boolean } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  email: text("email").notNull().unique(),
  password: text("password"),
  name: text("name").notNull(),
  googleId: text("google_id").unique(),
  avatar: text("avatar"),
  provider: text("provider").default("local"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const toolResults = pgTable("tool_results", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").references(() => users.id),
  toolType: text("tool_type").notNull(),
  query: text("query").notNull(),
  results: jsonb("results").notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const userSessions = pgTable("user_sessions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").references(() => users.id).notNull(),
  token: text("token").notNull().unique(),
  expiresAt: timestamp("expires_at").notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const insertUserSchema = createInsertSchema(users).pick({
  email: true,
  password: true,
  name: true,
});

export const loginUserSchema = z.object({
  email: z.string().email(),
  password: z.string().min(6),
});

export const insertToolResultSchema = createInsertSchema(toolResults).pick({
  toolType: true,
  query: true,
  results: true,
});

export type InsertUser = z.infer<typeof insertUserSchema>;
export type LoginUser = z.infer<typeof loginUserSchema>;
export type User = typeof users.$inferSelect;
export type ToolResult = typeof toolResults.$inferSelect;
export type InsertToolResult = z.infer<typeof insertToolResultSchema>;
export type UserSession = typeof userSessions.$inferSelect;

// Competition Checker Interfaces
export interface CompetitorData {
  name: string;
  domain: string;
  url: string;
  rank: number;
  pa: number;
  da: number;
  backlinks: number;
  referringDomains: number;
  organicKeywords: number;
}

export interface CompetitionAnalysis {
  targetDomain: string;
  keywords: string[];
  country: string;
  competitors: CompetitorData[];
  keywordAnalysis: {
    keyword: string;
    difficulty: number;
    searchVolume: number;
    topCompetitors: {
      domain: string;
      position: number;
      url: string;
      title: string;
    }[];
  }[];
  summary: {
    totalCompetitors: number;
    averageDA: number;
    averagePA: number;
    topCompetitorsByDA: CompetitorData[];
    keywordGaps: string[];
  };
}

// Top Search Queries Interfaces
export interface SearchQueryData {
  keyword: string;
  rank: number;
  cpc: number;
  difficulty: number;
  monthlyVolume: number;
  clicks: number;
  url: string;
  searchVolume: number;
  trend: string;
}

export interface ReferrerData {
  url: string;
  domain: string;
  backlinks: number;
  domainAuthority: number;
  firstSeenDate: Date | null;
  lastSeenDate: Date | null;
  linkType: 'dofollow' | 'nofollow';
  anchorText: string;
  pageTitle: string;
}

export interface TopSearchQueriesAnalysis {
  domain: string;
  country: string;
  totalQueries: number;
  queries: SearchQueryData[];
  summary: {
    averageRank: number;
    totalClicks: number;
    averageCPC: number;
    highVolumeKeywords: number;
    competitiveKeywords: number;
  };
}

