    background: var(--mint-primary);
    color: white;
    border: none;
  }

  .mint-button:hover {
    background: var(--mint-dark);
    transform: translateY(-2px);
  }

  .mint-button-outline {
    @apply px-8 py-4 rounded-full font-medium transition-all duration-300 border-2;
    background: transparent;
    color: var(--mint-primary);
    border-color: var(--mint-primary);
  }

  .mint-button-outline:hover {
    background: var(--mint-primary);
    color: white;
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(34, 197, 94, 0.3);
  }

  .feature-icon {
    @apply w-16 h-16 rounded-full flex items-center justify-center mb-4 mx-auto;
    background: var(--mint-light);
    color: var(--mint-primary);
  }

  .section-padding {
    @apply py-20;
  }

  .container-width {
    @apply max-w-7xl mx-auto px-4 sm:px-6 lg:px-8;
  }

  .smooth-hover {
    @apply transition-all duration-300 ease-out;
  }

  /* Better spacing for content pages */
  .content-spacing {
    @apply space-y-6;
  }

  .content-spacing p {
    @apply mb-6 leading-relaxed;
  }

  .content-spacing h2 {
    @apply mt-12 mb-6 text-2xl font-bold;
  }

  .content-spacing h3 {
    @apply mt-8 mb-4 text-xl font-semibold;
  }

  .content-spacing ul, .content-spacing ol {
    @apply mb-6 pl-6 space-y-2;
  }

  .content-spacing li {
    @apply leading-relaxed;
  }

  .content-spacing code {
    @apply bg-gray-100 px-2 py-1 rounded text-sm font-mono;
  }

  .content-spacing pre {
    @apply bg-gray-100 p-4 rounded-lg overflow-x-auto mb-6;
  }

  .text-gradient {
    background: var(--gradient-mint);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  /* Legacy support */
  .gradient-bg {
    background: var(--gradient-mint);
  }

  .tool-card {
    @apply mint-card cursor-pointer;
  }

  .material-button-primary {
    @apply mint-button;
  }

  .material-button-secondary {
    @apply mint-button-outline;
  }
}

@keyframes loading {
  0% { background-position: 200% 0; }
  100% { background-position: -200% 0; }
}

