import { useState } from "react";
import { useAuth } from "@/lib/auth";
import { Button } from "@/components/ui/button";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { Menu, User, LogOut, Search } from "lucide-react";
import { Link } from "wouter";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

export default function Header() {
  const { user, isAuthenticated, logout } = useAuth();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const logoutMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest("POST", "/api/auth/logout");
      return response.json();
    },
    onSettled: () => {
      logout();
      window.location.href = "/";
    },
  });

  const handleLogout = () => {
    logoutMutation.mutate();
  };

  return (
    <header className="bg-white border-b border-gray-100 sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          {/* Logo and Brand */}
          <Link href="/">
            <div className="flex items-center space-x-3 cursor-pointer">
              <div className="w-10 h-10 bg-gradient-to-br from-green-500 to-emerald-600 rounded-xl flex items-center justify-center shadow-lg">
                <span className="text-white font-bold text-lg">R</span>
              </div>
              <div className="flex flex-col">
                <span className="text-xl font-bold text-gray-900 leading-tight">RankBee</span>
                <span className="text-xs text-gray-600 font-medium">Professional Tools</span>
              </div>
            </div>
          </Link>

          {/* Desktop Navigation */}
          <nav className="hidden md:flex items-center space-x-8">
            <a 
              href="#tools" 
              className="text-gray-600 hover:text-green-600 transition-colors cursor-pointer"
              onClick={(e) => {
                e.preventDefault();
                const toolsSection = document.getElementById('tools');
                if (toolsSection) {
                  toolsSection.scrollIntoView({ behavior: 'smooth' });
                } else {
                  window.location.href = '/#tools';
                }
              }}
            >
              Tools
            </a>
            <Link href="/blogs" className="text-gray-600 hover:text-green-600 transition-colors">
              Features
            </Link>
            <Link href="/about" className="text-gray-600 hover:text-green-600 transition-colors">
              About
            </Link>
            <Link href="/contact" className="text-gray-600 hover:text-green-600 transition-colors">
              Contact
            </Link>
          </nav>

          {/* Right Side - Search and Get Started */}
          <div className="hidden md:flex items-center space-x-4">
            <div className="relative">
              <input
                type="text"
                placeholder="Quick Search"
                className="pl-8 pr-4 py-2 border border-gray-200 rounded-lg text-sm w-48 focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-transparent"
                onChange={(e) => {
                  const searchTerm = e.target.value.toLowerCase().trim();
                  // Scroll to tools section and filter
                  const toolsSection = document.getElementById('tools');
                  if (toolsSection && searchTerm) {
                    setTimeout(() => {
                      toolsSection.scrollIntoView({ behavior: 'smooth' });
                    }, 100);

                    // Filter tool cards
                    const toolCards = document.querySelectorAll('[data-tool-card]');
                    toolCards.forEach((card) => {
                      const element = card as HTMLElement;
                      const toolTitle = element.getAttribute('data-tool-title')?.toLowerCase() || '';
                      const toolDescription = element.getAttribute('data-tool-description')?.toLowerCase() || '';
                      const toolCategory = element.querySelector('[data-tool-category]')?.textContent?.toLowerCase() || '';

                      if (toolTitle.includes(searchTerm) || toolDescription.includes(searchTerm) || toolCategory.includes(searchTerm)) {
                        element.style.display = 'block';
                        element.style.opacity = '1';
                        element.style.transform = 'scale(1)';
                      } else {
                        element.style.display = 'none';
                      }
                    });
                  } else if (!searchTerm) {
                    // Show all tools when search is cleared
                    const toolCards = document.querySelectorAll('[data-tool-card]');
                    toolCards.forEach((card) => {
                      const element = card as HTMLElement;
                      element.style.display = 'block';
                      element.style.opacity = '1';
                      element.style.transform = 'scale(1)';
                    });
                  }
                }}
              />
              <Search className="h-4 w-4 absolute left-2.5 top-2.5 text-gray-400" />
            </div>

            {isAuthenticated() ? (
              <DropdownMenu>
                <DropdownMenuTrigger asChild>
                  <Button variant="ghost" className="flex items-center space-x-2 hover:bg-green-50">
                    <div className="w-8 h-8 bg-green-100 rounded-full flex items-center justify-center">
                      <User className="h-4 w-4 text-green-600" />
                    </div>
                    <span className="font-medium">{user?.name}</span>
                  </Button>
                </DropdownMenuTrigger>
                <DropdownMenuContent align="end" className="w-48">
                  <DropdownMenuItem onClick={handleLogout} className="text-red-600">
                    <LogOut className="h-4 w-4 mr-2" />
                    Sign Out
                  </DropdownMenuItem>
                </DropdownMenuContent>
              </DropdownMenu>
            ) : (
              <Link href="/auth">
                <Button className="mint-button">
                  Get Started
                </Button>
              </Link>
            )}
          </div>

          {/* Mobile Menu Button */}
          <Button
            variant="ghost"
            size="sm"
            className="md:hidden"
            onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
          >
            <Menu className="h-5 w-5" />
          </Button>
        </div>

        {/* Mobile Menu */}
        {isMobileMenuOpen && (
          <div className="md:hidden py-4 border-t border-gray-200">
            <div className="flex flex-col space-y-3">
              <a 
                href="#tools" 
                className="text-gray-600 hover:text-green-600 transition-colors cursor-pointer"
                onClick={(e) => {
                  e.preventDefault();
                  setIsMobileMenuOpen(false);
                  const toolsSection = document.getElementById('tools');
                  if (toolsSection) {
                    toolsSection.scrollIntoView({ behavior: 'smooth' });
                  } else {
                    window.location.href = '/#tools';
                  }
                }}
              >
                Tools
              </a>
              <Link href="/blogs" className="text-gray-600 hover:text-green-600 transition-colors">
                SEO Guides
              </Link>
              <Link href="/about" className="text-gray-600 hover:text-green-600 transition-colors">
                About
              </Link>
              <Link href="/contact" className="text-gray-600 hover:text-green-600 transition-colors">
                Contact
              </Link>

              <div className="pt-2 border-t border-gray-200">
                {isAuthenticated() ? (
                  <div className="space-y-2">
                    <p className="text-sm text-gray-600">Signed in as {user?.name}</p>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={handleLogout}
                      className="w-full justify-start"
                    >
                      <LogOut className="h-4 w-4 mr-2" />
                      Sign Out
                    </Button>
                  </div>
                ) : (
                  <Link href="/auth">
                    <Button className="w-full material-button-primary">
                      Sign In
                    </Button>
                  </Link>
                )}
              </div>
            </div>
          </div>
        )}
      </div>
    </header>
  );
}

client/src/components/seo-tools/backlink.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Loader2, ExternalLink, Target, Link2, AlertCircle } from "lucide-react";
import { getAuthHeaders } from "@/lib/auth";

interface LinkDetail {
  url: string;
  type: 'internal' | 'external';
  rel: string[];
  anchorText: string;
}

interface BacklinkResult {
  totalLinks: number;
  internalLinks: LinkDetail[];
  externalLinks: LinkDetail[];
  domain: string;
  url: string;
}

interface BacklinkFormData {
  domain: string;
}

export default function BacklinkAnalyzer() {
  const [results, setResults] = useState<BacklinkResult | null>(null);
  const { register, handleSubmit, formState: { errors } } = useForm<BacklinkFormData>({
    defaultValues: {
      domain: '',
    },
  });

  const backlinkMutation = useMutation({
    mutationFn: async (data: BacklinkFormData) => {
      const response = await fetch('/api/tools/backlink-analyzer', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          ...getAuthHeaders(),
        },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to analyze backlinks');
      }

      return response.json();
    },
    onSuccess: (data) => {
      setResults(data);
    },
  });

  const onSubmit = (data: BacklinkFormData) => {
    backlinkMutation.mutate(data);
  };

  const getLinkTypeColor = (type: 'internal' | 'external') => {
    return type === 'internal' 
      ? 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-300'
      : 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300';
  };

  const isDofollow = (rel: string[]) => {
    return !rel.includes('nofollow');
  };

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Link2 className="h-5 w-5" />
            Backlink Analyzer
          </CardTitle>
          <p className="text-sm text-muted-foreground">
            Real-time analysis of internal and external links by scraping the webpage
          </p>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
            <div>
              <Label htmlFor="domain">Website URL to Analyze</Label>
              <div className="flex gap-4">
                <Input
                  id="domain"
                  placeholder="https://example.com"
                  className="flex-1"
                  {...register('domain', { 
                    required: 'URL is required',
                    pattern: {
                      value: /^https?:\/\/.+/,
                      message: 'Please enter a valid URL'
                    }
                  })}
                />
                <Button
                  type="submit"
                  className="material-button-primary"
                  disabled={backlinkMutation.isPending}
                >
                  {backlinkMutation.isPending ? (
                    <>
                      <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                      Analyzing...
                    </>
                  ) : (
                    <>
                      <Target className="h-4 w-4 mr-2" />
                      Analyze Links
                    </>
                  )}
                </Button>
              </div>
              {errors.domain && (
                <p className="text-sm text-red-600 mt-1">{errors.domain.message}</p>
              )}
            </div>
          </form>
        </CardContent>
      </Card>

      {/* Loading State */}
      {backlinkMutation.isPending && (
        <Card>
          <CardContent className="pt-6">
            <div className="space-y-4">
              <div className="loading-skeleton h-16 rounded" />
              <div className="space-y-3">
                {[...Array(5)].map((_, i) => (
                  <div key={i} className="loading-skeleton h-12 rounded" />
                ))}
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Error State */}
      {backlinkMutation.isError && (
        <Card className="border-red-200 dark:border-red-800">
          <CardContent className="pt-6">
            <div className="flex items-center gap-2 text-red-600 dark:text-red-400">
              <AlertCircle className="h-4 w-4" />
              <p>Error: {backlinkMutation.error?.message || 'Failed to analyze backlinks'}</p>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Results */}
      {results && (
        <div className="space-y-6">
          {/* Overview */}
          <Card>
            <CardHeader>
              <CardTitle>Link Overview</CardTitle>
              <p className="text-sm text-muted-foreground">
                {results.domain} â€¢ {results.url}
              </p>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div className="text-center space-y-2">
                  <div className="text-2xl font-bold text-blue-600 dark:text-blue-400">
                    {results.totalLinks.toLocaleString()}
                  </div>
                  <div className="text-sm text-muted-foreground">Total Links</div>
                </div>

                <div className="text-center space-y-2">
                  <div className="text-2xl font-bold text-green-600 dark:text-green-400">
                    {results.internalLinks.length.toLocaleString()}
                  </div>
                  <div className="text-sm text-muted-foreground">Internal Links</div>
                </div>

                <div className="text-center space-y-2">
                  <div className="text-2xl font-bold text-purple-600 dark:text-purple-400">
                    {results.externalLinks.length.toLocaleString()}
                  </div>
                  <div className="text-sm text-muted-foreground">External Links</div>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Internal Links */}
          {results.internalLinks.length > 0 && (
            <Card>
              <CardHeader>
                <CardTitle>Internal Links ({results.internalLinks.length})</CardTitle>
                <p className="text-sm text-muted-foreground">
                  Links pointing to pages within the same domain
                </p>
              </CardHeader>
              <CardContent>
                <div className="space-y-4 max-h-96 overflow-y-auto">
                  {results.internalLinks.slice(0, 20).map((link, index) => (
                    <div key={index} className="p-4 border rounded-lg space-y-2">
                      <div className="flex items-start justify-between gap-4">
                        <div className="flex-1">
                          <div className="flex items-center gap-2 mb-2">
                            <Badge className={getLinkTypeColor(link.type)}>
                              {link.type}
                            </Badge>
                            {isDofollow(link.rel) ? (
                              <Badge variant="default" className="bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300">
                                Dofollow
                              </Badge>
                            ) : (
                              <Badge variant="secondary">
                                Nofollow
                              </Badge>
                            )}
                          </div>
                          <p className="text-sm font-medium break-all">{link.url}</p>
                          {link.anchorText && (
                            <p className="text-sm text-muted-foreground mt-1">
                              "{link.anchorText}"
                            </p>
                          )}
                          {link.rel.length > 0 && (
                            <p className="text-xs text-muted-foreground mt-1">
                              rel: {link.rel.join(', ')}
                            </p>
                          )}
                        </div>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => window.open(link.url, '_blank')}
                        >
                          <ExternalLink className="h-4 w-4" />
                        </Button>
                      </div>
                    </div>
                  ))}
                  {results.internalLinks.length > 20 && (
                    <p className="text-sm text-muted-foreground text-center py-4">
                      Showing first 20 of {results.internalLinks.length} internal links
                    </p>
                  )}
                </div>
              </CardContent>
            </Card>
          )}

          {/* External Links */}
          {results.externalLinks.length > 0 && (
            <Card>
              <CardHeader>
                <CardTitle>External Links ({results.externalLinks.length})</CardTitle>
                <p className="text-sm text-muted-foreground">
                  Links pointing to external domains
                </p>
              </CardHeader>
              <CardContent>
                <div className="space-y-4 max-h-96 overflow-y-auto">
                  {results.externalLinks.slice(0, 20).map((link, index) => (
                    <div key={index} className="p-4 border rounded-lg space-y-2">
                      <div className="flex items-start justify-between gap-4">
                        <div className="flex-1">
                          <div className="flex items-center gap-2 mb-2">
                            <Badge className={getLinkTypeColor(link.type)}>
                              {link.type}
                            </Badge>
                            {isDofollow(link.rel) ? (
                              <Badge variant="default" className="bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300">
                                Dofollow
                              </Badge>
                            ) : (
                              <Badge variant="secondary">
                                Nofollow
                              </Badge>
                            )}
                          </div>
                          <p className="text-sm font-medium break-all">{link.url}</p>
                          {link.anchorText && (
                            <p className="text-sm text-muted-foreground mt-1">
                              "{link.anchorText}"
                            </p>
                          )}
                          {link.rel.length > 0 && (
                            <p className="text-xs text-muted-foreground mt-1">
                              rel: {link.rel.join(', ')}
                            </p>
                          )}
                        </div>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => window.open(link.url, '_blank')}
                        >
                          <ExternalLink className="h-4 w-4" />
                        </Button>
                      </div>
                    </div>
                  ))}
                  {results.externalLinks.length > 20 && (
                    <p className="text-sm text-muted-foreground text-center py-4">
                      Showing first 20 of {results.externalLinks.length} external links
                    </p>
                  )}
                </div>
              </CardContent>
            </Card>
          )}

          {/* No Links Found */}
          {results.totalLinks === 0 && (
            <Card>
              <CardContent className="pt-6">
                <div className="text-center text-muted-foreground">
                  <Link2 className="h-12 w-12 mx-auto mb-4 opacity-50" />
                  <p>No links found on this page.</p>
                </div>
              </CardContent>
            </Card>
          )}
        </div>
      )}
    </div>
  );
}

