          ...getAuthHeaders(),
        },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        throw new Error('Failed to generate keywords');
      }

      return response.json();
    },
    onSuccess: (data) => {
      setResults(data.suggestions);
    },
  });

